;$('document').ready(function(){$('#add_to_compare').click(function(e){e.preventDefault();var o={property_id:30};$.post('http://localhost/themes-customization/index.php/api/add_to_compare/en',o,function(e){ShowStatus.show(e.message);if(e.success){if(e.remove_first){$('.compare-list li').first().remove()};$('.compare-list').append('<li data-id="'+e.property_id+'"><a href="'+e.property_url+'">'+e.property+'</a></li>');$('#add_to_compare').css('display','none');$('#remove_from_compare').css('display','inline-block');if($('.compare-list li').length>1)$('.compare-content .btn2').css('display','inline-block')}});return!1});$('#remove_from_compare').click(function(e){e.preventDefault();var o={property_id:30};$.post('http://localhost/themes-customization/index.php/api/remove_from_compare/en',o,function(e){ShowStatus.show(e.message);if(e.success){$('.compare-list li').filter('[data-id="'+e.property_id+'"]').remove();$('#remove_from_compare').css('display','none');$('#add_to_compare').css('display','inline-block');if(!$('.compare-list li').length||$('.compare-list li').length<2)$('.compare-content .btn2').css('display','none')}});return!1})})